INSERT INTO `main_table` VALUES ('75', 'Short Stories', '', '', '', '', 'Mahmoud Manzalaoui', '', 'Maḥmūd Manzalāwī', '', 'Arabic writing today', 'the short story', '', '', '', 'American Research Center in Egypt', 'Cairo', '1971, 1968', 'The martyr (tr. David Bishai) / Tewfik el Hakim -- The enemy (tr. Anthony McDermott) / Mahmoud Taymour -- Atrocity (tr. Mahmoud Manzalaoui) / Mohamed Kamel Hussein -- The brass four-poster (tr. Nur Sherif) -- An empty bed (tr. Mahmoud Manzalaoui) / Yehia Hakki -- Nanny Karima and the Hammam (tr. Magdi Wahba) / Saheir El Kalamawi -- The mosque in the narrow lane (tr. Nadia Farag) -- Hanzal and the policeman (tr. Azza Kararah) / Naguib Mahfouz -- Just a puppet (tr. Nadia Farag) / Rashad Rushdi -- The master-milkman (tr. Nadia Farag) / Youssef El Sebai -- A boy\'s best friend (tr. Wadida Wassef) / Ihsan Abdel Kuddus -- Master loaf the cross-grained oaf (tr. Sami al-Kalyoubi) / Abdel Kader El Samihi -- The scorpion hunter (tr. David Bishai) / Abdel Rahman El Sharkawy -- Three men and a dog (tr. David Bishai) / Abdel Rahman Fahmy -- In the surgery (tr. author) / Shukry Ayyad -- Dearly beloved brethren! (tr. Sami al-Kalyoubi) / Haseeb El Kayyali -- By the water\'s edge (tr. Nadia Farag) / Edward El Kharrat -- The cheapest night\'s entertainment (tr. Nur Sherif) -- Peace with honour (tr. Nadia Farag) / Youssef Idris -- Trial by ordeal (tr. Wadida Wassef & Derek Hopwood) / Ghaleb Halassa -- Snow at night (tr. Medhat Shaheen) / Zakaria Tamer -- The night train home (tr. Mohamed Kaddal) / Mahmoud El Saadani -- The thief and the watchman (tr. Azza Kararah) / Soliman Fayyad -- Still another year (tr. Safia Rabie) / Samira Azzam -- A jailhouse of my own (tr. Wadida Wassef) / Ihsan Kamal -- Street walker (tr. Azza Kararah) / Ghada El Samman -- The crowd (tr. Nadia Farag) / Youssef El Sharouny -- Dunya (tr. Fatma Moussa) / Fathi Ghanem -- The fortune teller (tr. Medhat Shaheen) / Alaa Deeb -- The futility of it all (tr. Mahmoud Manzalaoui) / Farou