INSERT INTO `main_table` VALUES ('377', 'Novel', 'Mahfouz', 'Naguib', '', 'Najīb Maḥfūẓ', 'Jacqueline Kennedy Onassis', 'William Maynard Hutchins', '', '', 'Cairo trilogy', '', 'Bayn al-qaṣrayn; Qaṣr al-shawq; al-Sukkarīyah', 'Hutchins mss.', 'Egypt', '', '', '1972-1999', '', '', '', 'Consists of the manuscripts and proofs of translations from the Arabic made by William Maynard Hutchins, 1944- . The collection is organized into two series:  I. English translations of  Naguib Mahfouz’s Cairo Trilogy; and, II. Translations of other Arabic writers.  The major work represented here is his translation of all three volumes of Egyptian Nobel Prize winner Naguib Mahfouz’s Cairo Trilogy.  The editor for these volumes at Doubleday was Jacqueline Kennedy Onassis.  Editorial comments and queries in her hand are on each of the three manuscripts.  Other Arabic, particularly Egyptian, authors translated by Hutchins include Tawfīq al-Hakīm, Abu Uthman Amr ibn al-Jahiz, and Nawal Sadawi.\n\nThe Cairo trilogy is in boxes 1-7.', '', '', '', '', '10/14/04', 'OK', 'book');
