INSERT INTO `main_table` VALUES ('751', 'Short Stories', '', '', '', '', '', 'Denys Johnson-Davies et al.', '', '', 'Modern Arab stories', '', '', '', '', 'Ur', 'London', '1980', 'Who\'ll hang the bell / Yahya Tahir Abdullah -- The billy-goat and the menfolk / Samira al-Mana -- Tomorrow / Daisy al-Amer -- From behind the veil / Dhu\'l Nun Ayyoub -- The evening of that noon / Mohamed Barrada -- Arnoun / Saad al-Bazzaz -- The slave fort / Ghassan Kanafani -- The marsh god / Muhammed Khudair -- The trees / Abdul Rahman Munif -- When the clouds were on the point of tears / Abdullah Niazi -- Fatouma / Abdul Malik Nouri -- The tigers on the tenth day / Zakaria Tamer.', 'Man yuʻalliq al-jaras / Yaḥyā Ṭāhir ʻAbd Allāh -- al-ʻAnz wa-al-rijāl / Samīrah al-Māniʻ -- Ghadan / Dayzī al-Amīr -- Min warāʼ al-ḥijāb / Dhū al-Nūn Ayyūb -- Masāʼ tilka al-ẓahīrah / Muḥammad Barādah -- Arnūn / Saʻd al-Bazzāz -- Qalʻat al-ʻabīd / Ghassān Kanafānī -- Ilāh al-mustanqa‘āt / Muḥammad Khuḍayr -- al-Ashjār / ʻAbd al-Raḥmān Munīf -- ‘Indamā kānat al-suḥub ‘alá washk al-bukā’ / ʻAbd Allāh Niyāzī -- Faṭṭūmah / ʻAbd al- Malik Nūrī -- Numūr fī al-yawm al-ʻāshir / Zakarīyā Tāmir.', '', 'Stories translated by Denys Johnson-Davies, John Fletcher, Ali M. Cassidy, W. McClung Frazier, Frida Abu-Haidar, Rabah Munir Shaikh al-Ard.', '', '132 p. ; 20 cm.', '', '0905206509 (pbk)', '03/07/05', 'OK-Checked', 'book');
