INSERT INTO `main_table` VALUES ('848', 'Verse', '', '', '', '', '', 'James T. Monroe', '', '', 'Hispano-Arabic poetry', 'a student anthology', '', '', 'Spain', 'Gorgias Press', 'Piscataway, NJ', '2004', 'Biographical notes -- Abbreviations -- Introduction -- Ibn \'Abd Rabbihi -- Ibn Hani\' -- Ibn Darraj al-Qastalli -- Ash-Sharif at-Taliq -- Ibn Shuhaid -- Ibn Shuhaid -- Ibn Shuhaid -- Ibn Hazm -- Ibn Zaidun -- Ibn \'Ammar -- al-Mu\'tamid -- al-Mu\'tamid -- Ibn Hamdis -- Ibn Hamdis -- Abu Ishaq -- Ibn al-Labbana -- Ibn \'Ubada al-Qazzaz -- Ibn \'Ubada al-Qazzaz -- Ibn Arfa\' Ra\'suh -- Ibn \'Abdun -- Ibn Khafaja -- Ibn az-Zaqqaq -- al-A\'ma at-Tutili  -- al-A\'ma at-Tutili -- Ibn Baqi -- Ibn Quzman  -- Ibn Quzman -- al-Abyad -- Ibn Bajja -- Ibn Zuhr -- ar-Rusafi -- Hatim Ibn Sa\'id -- Ibn Sahl -- Shushtari  -- Shushtari -- Ibn al-\'Arabi -- Hazim al-Qartajanni -- ar-Rundi -- Ibn al-Khatib -- Ibn Zamrak -- Yusuf III  -- Yusuf III -- Anonymous -- Glossary of technical terms applied to Arabic poetry -- Bibliography.', 'Aḥmad ibn Muḥammad Abū ‘Umar ibn ‘Abd Rabbihi (860-940) -- Abū al-Qāsim Muḥammad Ibn Hānī’ al-Andalusī (ca. 932/937-973) -- Abū ‘Umar Aḥmad ibn Muḥammad ibn ‘Aṣī ibn Aḥmad ibn Sulaymān ibn Darrāj al-Qasṭallī (958-1030) -- Marwān ibn ‘Abd al-Raḥmān ibn Marwān ibn ‘Abd al-Raḥmān al-Nāṣir Abū ‘Abd al-Mālik al-Sharīf al-Ṭalīq (ca. 961-1009) -- Abū ‘Āmir Aḥmad ibn Abī Marwān ‘Abd al-Malik al-Ashja‘ī ibn Shuhayd (992-1035) -- Abū Muḥammad ‘Alī ibn Ḥazm al-Andalusī (994-1064) -- Abū al-Walīd Aḥmad ibn ‘Abd Allāh ibn Aḥmad ibn Ghālib al-Makhzūmī ibn Zaydūn (1003-1071) -- Abū Bakr ibn ‘Ammār (1031-1084) -- Abū al-Qāsim Muḥammad al-Mu‘tamid ‘alá Allāh ibn ‘Abbād (1040-1095) -- ‘Abd al-Jabbār ibn Abī Bakr ibn Muḥammad al-Azdī ibn Ḥamdīs (1058-113) -- Ibrāhīm ibn Mas‘ūd ibn Sa‘īd al-Tujībī Abū Isḥāq al-Ilbīrī (d. 1067) -- Abū Bakr Muḥammad ibn ‘Īsá al-Dānī ibn al-Labbānah (d. 1113) -- Abū ‘Abd Allāh Muḥammad ‘Ubādah al-Qazzāz -- Abū Bakr Muḥammad ibn Arfa‘ Ra’suh --